import React from 'react';
import axios from 'axios';
import "./Post.css";

export default class PhotosList extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      photos: [],
      users: [],
      photoUser:[]
    }
  }
  componentDidMount() {
    axios.all([
      axios.get(`https://jsonplaceholder.typicode.com/users`),
      axios.get(`https://jsonplaceholder.typicode.com/photos`)
    ])
    .then(axios.spread((resUsers, resPhotos) => {
      const users = resUsers.data;
      this.setState({ users });
      const photos = resPhotos.data;
      this.setState({ photos });
      this.mergeArray();
    }))
  }

  mergeArray(){
    let merge = []
    this.state.photos.forEach(function(photo) {
      let photoName = {};
      console.log(photo)
      this.state.users.forEach(function(user) {
        if(photo.id == user.id) {
          photoName = {
            id : photo.id,
            url: photo.url,
            title: photo.title,
            name: user.name
          }
        }
      });
      merge.push(photoName);
    });   
    console.table(merge)
  }


  render() {
    return (
      <article className="Post" ref="Post">
      <ul>
      <div className="Post-image">
        <div className="Post-image-bg">
          {this.state.photoUser.slice(0,5).map(photo => <li key={photo.id}><img src={photo.url} /><p> {photo.title}</p><p>{photo.name}</p></li>)}
        </div>
      </div>
      </ul>
     </article>
    )
  }};
