import React from 'react';
import axios from 'axios';

export default class PersonList extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      titles: [],
      photos: [],
    }
  }
  componentDidMount() {
    axios.all([
      axios.get(`https://pixabay.com/api/?key=10399720-74b4e8e8c92d25cd730b2aaae&q=corgi&image_type=photo&pretty=true`),
      axios.get(`https://jsonplaceholder.typicode.com/photos`)
    ])
    .then(axios.spread((resPhotos, resTitles) => {
      const photos = resPhotos.data;
      this.setState({ photos });
      const titles = resTitles.data;
      this.setState({ titles });
       this.mergeArray();
    }))
  }

  render() {
    return (
      <article className="Post" ref="Post">
      <ul>
      <div className="Post-image">
        <div className="test">
          {this.state.photos.slice(0,5).map(photo => <li className="elements" key={photo.id}><div className="user"><img className="icon" src={photo.userImageURL}/><p className="name">{photo.user}</p></div><img className="image" src={photo.pageURL} /><div className="legend"><p className="name">{photo.user}</p><p className="title"> {photo.type}</p></div></li>)}
        </div>
      </div>
      </ul>
     </article>
    )
  }
}
