import React from 'react';
import axios from 'axios';
import "./Post.css";

export default class PhotosList extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      photos: [],
      users: []
    }
  }
  componentDidMount() {
    axios.all([
      axios.get(`https://jsonplaceholder.typicode.com/users`),
      axios.get(`https://jsonplaceholder.typicode.com/photos`)
    ])
    .then(axios.spread((resUsers, resPhotos) => {
      const users = resUsers.data;
      this.setState({ users });
      const photos = resPhotos.data;
      this.setState({ photos });
      this.mergeArray()
    }))
    .catch((error)=>{
      console.log(error)
    });
  }

  mergeArray(){
    console.log(this.state.photos)
    console.log(this.state.users)
  }

  render() {
    return (
      <article className="Post" ref="Post">
      <ul>
      <div className="Post-image">
        <div className="Post-image-bg">
          {this.state.users.slice(0,5).map(user => {user.name})};
          {this.state.photos.slice(0,5).map(photo => <li key={photo.id}><img src={photo.url} /><p> {photo.title}</p></li>)}
        </div>
      </div>
      </ul>
     </article>
    )
  }
}
